elif self.T > 0 and self.T < 1:
     raise ValueError("T must be either 0 or above 1 (got {})".format(self.T))
elif self.T < 0:
    raise ValueError("T must be nonnegative (got {})".format(self.T))
elif self.T == 0:
    self.average = False
    self.T = 2**(self.J)
else:
    self.average = True
self.log2_T = math.floor(math.log2(self.T))
