    Kokkos::View<int32_t*> image_data("image_data", 270000);
    i32_600_450_2 image_value(&image_data);
    i32_600_450_2* image = &image_value;
    image->dims[0].lower_bound = 1;
    image->dims[0].length = 600;
    image->dims[1].lower_bound = 1;
    image->dims[1].length = 450;
    Kokkos::View<int32_t*> image_color_data("image_color_data", 1080000);
    i32_4_600_450_3 image_color_value(&image_color_data);
    i32_4_600_450_3* image_color = &image_color_value;
    image_color->dims[0].lower_bound = 1;
    image_color->dims[0].length = 4;
    image_color->dims[1].lower_bound = 1;
    image_color->dims[1].length = 600;
    image_color->dims[2].lower_bound = 1;
    image_color->dims[2].length = 450;
    int32_t j;
    int32_t n;
    int32_t n_max=255;
    int32_t nx=600;
    int32_t ny=450;
    Kokkos::View<int32_t*> palette_data("palette_data", 12);
    i32_3_4_2 palette_value(&palette_data);
    i32_3_4_2* palette = &palette_value;
    palette->dims[0].lower_bound = 1;
    palette->dims[0].length = 3;
    palette->dims[1].lower_bound = 1;
    palette->dims[1].length = 4;
    double width=(double)(4);
    double x;
    double x_0;
    double x_offset=xcenter - (double)(nx + 1)*dx_di/(double)(2);
    double x_sqr;
    double xcenter=-  5.00000000000000000e-01;
    double y;
    double y_0;
    double y_offset=ycenter - (double)(ny + 1)*dy_dj/(double)(2);
    double y_sqr;
    double ycenter=  0.00000000000000000e+00;
    for (j=1; j<=ny; j++) {
        y_0 = y_offset + dy_dj*(double)(j);
        for (i=1; i<=nx; i++) {
            x_0 = x_offset + dx_di*(double)(i);
            x = (double)(0);
            y = (double)(0);
            n = 0;
            while (true) {
                x_sqr = std::pow(x, (double)(2));
                y_sqr = std::pow(y, (double)(2));
                if (x_sqr + y_sqr > (double)(4) || n == n_max) {
                    image->data->operator[](i - image->dims[0].lower_bound, j - image->dims[1].lower_bound) = 255 - n;
                    break;
                }
                y = y_0 + (double)(2)*x*y;
                x = x_0 + x_sqr - y_sqr;
                n = n + 1;
            }
        }
    }
    palette->data->operator[](1 - palette->dims[0].lower_bound, 1 - palette->dims[1].lower_bound) = 0;
    palette->data->operator[](2 - palette->dims[0].lower_bound, 1 - palette->dims[1].lower_bound) = 135;
    palette->data->operator[](3 - palette->dims[0].lower_bound, 1 - palette->dims[1].lower_bound) = 68;
    palette->data->operator[](1 - palette->dims[0].lower_bound, 2 - palette->dims[1].lower_bound) = 0;
    palette->data->operator[](2 - palette->dims[0].lower_bound, 2 - palette->dims[1].lower_bound) = 87;
    palette->data->operator[](3 - palette->dims[0].lower_bound, 2 - palette->dims[1].lower_bound) = 231;
    palette->data->operator[](1 - palette->dims[0].lower_bound, 3 - palette->dims[1].lower_bound) = 214;
