    palette->data->operator[](1 - palette->dims[0].lower_bound, 4 - palette->dims[1].lower_bound) = 255;
    palette->data->operator[](2 - palette->dims[0].lower_bound, 4 - palette->dims[1].lower_bound) = 167;
    palette->data->operator[](3 - palette->dims[0].lower_bound, 4 - palette->dims[1].lower_bound) = 0;
    for (j=1; j<=ny; j++) {
        for (i=1; i<=nx; i++) {
            idx = image->data->operator[](i - image->dims[0].lower_bound, j - image->dims[1].lower_bound) - image->data->operator[](i - image->dims[0].lower_bound, j - image->dims[1].lower_bound)/4*4 + 1;
            image_color->data->operator[](1 - image_color->dims[0].lower_bound, i - image_color->dims[1].lower_bound, j - image_color->dims[2].lower_bound) = palette->data->operator[](1 - palette->dims[0].lower_bound, idx - palette->dims[1].lower_bound);
            image_color->data->operator[](2 - image_color->dims[0].lower_bound, i - image_color->dims[1].lower_bound, j - image_color->dims[2].lower_bound) = palette->data->operator[](2 - palette->dims[0].lower_bound, idx - palette->dims[1].lower_bound);
            image_color->data->operator[](3 - image_color->dims[0].lower_bound, i - image_color->dims[1].lower_bound, j - image_color->dims[2].lower_bound) = palette->data->operator[](3 - palette->dims[0].lower_bound, idx - palette->dims[1].lower_bound);
            image_color->data->operator[](4 - image_color->dims[0].lower_bound, i - image_color->dims[1].lower_bound, j - image_color->dims[2].lower_bound) = 255;
        }
    }
    std::cout << "The Mandelbrot image in color:" << std::endl;
    show_img_color(nx, ny, image_color);
    std::cout << "The Mandelbrot image in grayscale:" << std::endl;
    show_img(nx, ny, image);
    std::cout << "Done." << std::endl;
}

