values = self.to_pandas()

if isinstance(values, pd.DatetimeIndex):
    freq = _check_freq(values)
    try:
        # coerce to pd.Period for easier arithmetics
        absolute = values.to_period(freq)
        cutoff = cutoff.to_period(freq)
        
    except ValueError:
