    VideoCapture cap("/home/taylan/out_fisheye_test.avi");


    if(!cap.isOpened()){
      cout << "Error opening video stream or file" << endl;
      return -1;
    }
    std::cout << "here" << std::endl;
    cap.set(CAP_PROP_POS_FRAMES, 40);

    int counter = 0;
    while(true){
        counter++;
        QString filename = QString::number(counter);

        Mat frame;
        cap >> frame;

        if (frame.empty()){
            break;
